import axios from "axios";
import React, { useState } from "react";
import { LOGINADMIN, baseUrl } from "../../Api/Api";
import "./Auth.css";
export default function LoginAdmin() {
  const [form, setForm] = useState({
    email: "",
    password: "",
  });
  const [loading, Setloading] = useState(false);
  const [errorLogin, setErrorLogin] = useState("");

  function changehandle(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }
  async function handleSubmit(e) {
    e.preventDefault();
    Setloading(true);
    try {
      await axios.post(`${baseUrl}/${LOGINADMIN}`, form);
      Setloading(false);
    } catch (error) {
      Setloading(false);
      if (error.response.status === 422) {
        setErrorLogin(error.response.data.message);
      }
    }
  }

  return (
    <div
      className="container mt-5"
      style={{ maxWidth: "400px" }}
      onSubmit={handleSubmit}
    >
      <h2>Admin Login</h2>
      {errorLogin && (
        <div className="error">
          {Object.keys(errorLogin).map((key) =>
            errorLogin[key].map((error, index) => (
              <span className="error" key={`${key}-${index}`}>
                {error}
              </span> // Use a combined key to ensure uniqueness
            ))
          )}
        </div>
      )}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="text"
            id="email"
            className="form-control"
            name="email"
            onChange={changehandle}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            className="form-control"
            name="password"
            onChange={changehandle}
            required
            minLength="8"
          />
        </div>
        <button type="submit" className="btn btn-primary mt-3">
          Login Admin
        </button>
      </form>
    </div>
  );
}
