import { CATEGORIES, baseUrl, DELETECATEGORY } from "../../../Api/Api.js";
// في أي ملف React آخر
import {
  Loading,
  FontAwesomeIcon,
  faPenToSquare,
  faTrash,
  useState,
  useEffect,
  Link,
  axios,
  Cookie,
  useNavigate,
  useParams,
} from "../Library/library.js";
import "../Auth/Auth.css";
export default function Categories() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [categories, setcategories] = useState([]);
  const [deleteCategory, setDeleteCategory] = useState(false);
  const cookie = Cookie();
  const token = cookie.get("admin-token");
  const configget = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "application/json",
    },
  };

  async function fetchData() {
    try {
      const response = await axios.get(`${baseUrl}/${CATEGORIES}`, configget);
      console.log(response.data);
      setcategories(response.data);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchData();
  }, [deleteCategory]);

  async function handelDelete(id) {
    try {
      setLoading(true);
      var response = await axios.delete(
        `${baseUrl}/${DELETECATEGORY}/${id}`,
        configget
      );
      if (response.status === 200) {
        setDeleteCategory((prev) => !prev);
        setLoading(false);
      }
    } catch (error) {
      console.log(error);
      setLoading(false);
    }
  }

  return (
    <>
      {loading && <Loading />}
      <div className="bg-white  w-100 p-2">
        <h1>Categories Page</h1>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Name</th>
              <th scope="col">ِAction</th>
            </tr>
          </thead>
          <tbody>
            {categories &&
              categories.map((item, index) => (
                <tr key={index}>
                  <td>{item.id}</td>
                  <th scope="row">{item.name}</th>
                  <td>{item.email}</td>
                  <td>
                    <div className="d-flex align-items-center gap-2">
                      <Link to={`../userProfile/${item.id}`}>
                        <FontAwesomeIcon
                          fontSize={"19px"}
                          color="blue"
                          icon={faPenToSquare}
                        />
                      </Link>
                      <FontAwesomeIcon
                        fontSize={"19px"}
                        onClick={() => handelDelete(`${item.id}`)}
                        color="red"
                        cursor={"pointer"}
                        icon={faTrash}
                      />
                    </div>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </>
  );
}
