import React, { useState } from "react";
import "./Auth.css";
import axios from "axios";
import { LOGINUSER, baseUrl } from "../../Api/Api";
import Loading from "../../components/Loading/Loading";

export default function LoginUser() {
  const [form, setForm] = useState({
    email: "",
    password: "",
  });
  const [loading, Setloading] = useState(false);
  const [errorLogin, setErrorLogin] = useState();
  function changehandle(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }
  async function handleSubmit(e) {
    e.preventDefault();
    Setloading(true);
    try {
      await axios.post(`${baseUrl}/${LOGINUSER}`, form);
      console.log("succsucce");
      Setloading(false);
    } catch (error) {
      Setloading(false);
      if (error.response.status === 422) {
        setErrorLogin(error.response.data.message);
      }
    }
  }
  return (
    <>
      {loading && <Loading />}
      <div className="container mt-5" style={{ maxWidth: "400px" }}>
        <h2>User Login</h2>
        {errorLogin && (
          <div className="error">
            {Object.keys(errorLogin).map((key) =>
              errorLogin[key].map((error, index) => (
                <span className="error" key={`${key}-${index}`}>
                  {error}
                </span> // Use a combined key to ensure uniqueness
              ))
            )}
          </div>
        )}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="text"
              id="email"
              className="form-control"
              name="email"
              onChange={changehandle}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              className="form-control"
              name="password"
              onChange={changehandle}
              required
              minLength="8"
            />
          </div>
          <button type="submit" className="btn btn-primary mt-3">
            Login
          </button>
        </form>
      </div>
    </>
  );
}
