import { useState, useEffect } from "react";
import axios from "axios";
import { baseUrl, userProfile, editProfile } from "../../Api/Api";
import "../Auth/Auth.css";
import Loading from "../../components/Loading/Loading";
import { useParams } from "react-router-dom";
import Cookie from "cookie-universal";

export default function UserProfile() {
  const { id } = useParams();
  console.log(id);
  const [loading, setLoading] = useState(false);
  const [errorRegister, setErrorRegister] = useState("");
  const [form, setForm] = useState({
    email: "",
    username: "",
    password: "",
    birthday: "",
    last_name: "",
    first_name: "",
    gender: "",
    address: "",
    state: "",
    city: "",
    postal_code: "",
    country: "",
  });
  const cookie = Cookie();
  const token = cookie.get("admin-token");
  useEffect(() => {
    const userId = id;

    fetchUserData(userId);
    console.log(form);
  }, []);

  async function fetchUserData(userId) {
    setLoading(true);
    try {
      const response = await axios.get(`${baseUrl}/${userProfile}/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });
      if (response.status === 200) {
        const profileData = response.data.profile[0];
        const userData = response.data.user;
        const formData = {
          address: profileData.address,
          birthday: profileData.birthday,
          city: profileData.city,
          country: profileData.country.toUpperCase(),
          email: userData.email,
          first_name: profileData.first_name,
          gender: profileData.gender,
          last_name: profileData.last_name,
          password: "", // الباسوورد عادة لا يتم تعبئته مسبقاً لأسباب أمنية
          postal_code: profileData.postal_code,
          state: profileData.state,
          username: userData.name,
        };
        setForm(formData);
        console.log(form);
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
      setErrorRegister("Failed to load user data");
    }
    setLoading(false);
  }

  function changhandle(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function submithandle(e) {
    e.preventDefault();
    setLoading(true);

    try {
      const result = await axios.put(`${baseUrl}/${editProfile}`, form);
      console.log(result);
      if (result.status === 200) {
        setForm({});
      }
    } catch (error) {
      console.error("Submission error:", error);
      setErrorRegister(
        error.response?.data?.errors || "Error occurred during registration"
      );
    }
    setLoading(false);
  }

  return (
    <>
      {loading && <Loading />}
      <div className="container mt-5 mb-5">
        <h2>Profile </h2>
        {errorRegister && (
          <div className="errorregister">
            {Object.keys(errorRegister).map(
              (key) =>
                Array.isArray(errorRegister[key]) ? (
                  errorRegister[key].map((error, index) => (
                    <span key={`${key}-${index}`}>{error}</span>
                  ))
                ) : (
                  <span key={`error-${key}`}>{errorRegister[key]}</span>
                ) // Handle non-array values gracefully
            )}
          </div>
        )}
        <div className="form-container">
          <form className="needs-validation" onSubmit={submithandle}>
            <div className="row">
              <div className="col">
                <div className="form-group">
                  <label htmlFor="username">Username:</label>
                  <input
                    type="text"
                    id="username"
                    name="username"
                    value={form.username}
                    onChange={changhandle}
                    className="form-control"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="email">Email:</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={form.email}
                    onChange={changhandle}
                    className="form-control"
                    required
                  />
                </div>
              </div>
              <div className="col">
                <div className="form-group">
                  <label htmlFor="password">Password:</label>
                  <input
                    type="password"
                    id="password"
                    name="password"
                    value={form.password}
                    onChange={changhandle}
                    className="form-control"
                    required
                    minLength="8"
                  />
                </div>
              </div>
            </div>

            <div className="row">
              <div className="col">
                <div className="form-group">
                  <label htmlFor="first_name">First Name:</label>
                  <input
                    type="text"
                    id="first_name"
                    name="first_name"
                    value={form.first_name}
                    onChange={changhandle}
                    className="form-control"
                    required
                  />
                </div>
              </div>
              <div className="col">
                <div className="form-group">
                  <label htmlFor="last_name">Last Name:</label>
                  <input
                    type="text"
                    id="last_name"
                    name="last_name"
                    value={form.last_name}
                    onChange={changhandle}
                    className="form-control"
                    required
                  />
                </div>
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="birthday">Birthday:</label>
              <input
                type="date"
                id="birthday"
                name="birthday"
                value={form.birthday}
                onChange={changhandle}
                className="form-control"
              />
            </div>

            <div className="form-group">
              <label htmlFor="gender">Gender:</label>
              <select
                id="gender"
                name="gender"
                value={form.gender}
                className="form-control"
                onChange={changhandle}
              >
                <option value="">Select Gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="address">Street Address:</label>
              <input
                type="text"
                id="address"
                name="address"
                onChange={changhandle}
                className="form-control"
              />
            </div>

            <div className="row">
              <div className="col">
                <div className="form-group">
                  <label htmlFor="city">City:</label>
                  <input
                    type="text"
                    id="city"
                    name="city"
                    value={form.city}
                    onChange={changhandle}
                    className="form-control"
                  />
                </div>
              </div>
              <div className="col">
                <div className="form-group">
                  <label htmlFor="state">State:</label>
                  <input
                    type="text"
                    id="state"
                    name="state"
                    value={form.state}
                    onChange={changhandle}
                    className="form-control"
                  />
                </div>
              </div>
            </div>

            <div className="row">
              <div className="col">
                <div className="form-group">
                  <label htmlFor="postal_code">Postal Code:</label>
                  <input
                    type="text"
                    id="postal_code"
                    name="postal_code"
                    value={form.postal_code}
                    onChange={changhandle}
                    className="form-control"
                  />
                </div>
              </div>
              <div className="col">
                <div className="form-group">
                  <label htmlFor="country">Country:</label>
                  <input
                    type="text"
                    id="country"
                    name="country"
                    value={form.country}
                    onChange={changhandle}
                    className="form-control"
                    maxLength="2"
                  />
                </div>
              </div>
            </div>

            <button type="submit" className="btn btn-primary mt-3">
              Update
            </button>
          </form>
        </div>
      </div>
    </>
  );
}
