import "./bars.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUsers,
  faWeightHanging,
  faLayerGroup,
} from "@fortawesome/free-solid-svg-icons";
import { NavLink } from "react-router-dom";
import { useContext } from "react";
import { Menu } from "../../Context/Menu";

export default function Sidebar() {
  const menu = useContext(Menu);
  const isopen = menu.isopen;
  return (
    <div className="side-bar p-2">
      <NavLink
        to={"users"}
        className="d-flex align-items-center gab-2 side-bar-link"
      >
        <FontAwesomeIcon
          icon={faUsers}
          style={{ padding: isopen ? "10px 8px 10px 15px" : "10px 4px" }}
        />
        <p className="m-2">users</p>
      </NavLink>
      <NavLink
        to={"products"}
        className="d-flex align-items-center gab-2 side-bar-link"
      >
        <FontAwesomeIcon icon={faWeightHanging} />
        <p className="m-2">products</p>
      </NavLink>

      <NavLink
        to={"categories"}
        className="d-flex align-items-center gab-2 side-bar-link"
      >
        <FontAwesomeIcon icon={faLayerGroup} />
        <p className="m-2">categories</p>
      </NavLink>
    </div>
  );
}
