import { baseUrl, CATEGORIES } from "../../../Api/Api";
import {
  Loading,
  FontAwesomeIcon,
  faPenToSquare,
  faTrash,
  useState,
  useEffect,
  Link,
  axios,
  Cookie,
  useNavigate,
  useParams,
  configget,
} from "../Library/library.js";
export default function CreateCategory() {
  const [form, setForm] = useState({
    name: "",
    status: "",
    category_id: "",
    description: "",
    imag: "",
  });
  const [categories, setCategories] = useState([{}]);
  const [loading, setLoading] = useState(false);
  const [errorcreateCategory, seterrorCatefory] = useState("");

  async function fetchData() {
    try {
      setLoading(true);
      const response = await axios.get(`${baseUrl}/${CATEGORIES}`, configget);
      console.log(response.data);
      if (response.data) {
        setCategories(response.data); // Store the entire array of categories
      }
      setLoading(false);
    } catch (error) {
      setLoading(false);
      seterrorCatefory(error.message);
      console.error(error);
    }
  }

  useEffect(() => {
    fetchData();
  }, []);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });

    console.log(form);
  }

  function handlesubmit() {
    console.log(categories);
  }
  return (
    <>
      {loading && <Loading />}

      <div className="container mt-5 mb-5">
        <h2>Add Category</h2>

        {errorcreateCategory && (
          <div className="errorregister">
            {Object.keys(errorcreateCategory).map((key) =>
              errorcreateCategory[key].map((error, index) => (
                <span key={`${key}-${index}`}>{error}</span> // Use a combined key to ensure uniqueness
              ))
            )}
          </div>
        )}
        <form onSubmit={handlesubmit}>
          <div className="form-group mb-3">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              name="name"
              id="name"
              value={form.name}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          <div className="form-group mb-3">
            <label htmlFor="category_id">Category Parent</label>
            <select
              name="category_id"
              className="form-control"
              style={{ height: "calc(2.8rem + 10px)" }}
              value={form.category_id || ""}
              onChange={handleChange}
            >
              <option value="">No Parent</option>
              {categories.map((category, index) => (
                <option key={index} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group mb-3">
            <label htmlFor="description">Description:</label>
            <textarea
              name="description"
              value={form.description}
              className="form-control "
              onChange={handleChange}
            ></textarea>
          </div>

          <div className="form-group mb-3">
            <label htmlFor="art_path">Image:</label>
            <input
              type="file"
              name="imag"
              id="image"
              className="form-control"
            />
          </div>

          <td>
            <img src="" height="50" />
          </td>

          <div class="form-group mb-2">
            <label htmlFor="status">Status:</label>
            <div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="status"
                  value="active"
                  onChange={handleChange}
                />
                <label className="form-check-label">Active</label>
              </div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="status"
                  value="inactive"
                  onChange={handleChange}
                />
                <label className="form-check-label">In-Active</label>
              </div>
            </div>
          </div>

          <button type="submit" className="btn btn-primary">
            ADD
          </button>
        </form>
      </div>
    </>
  );
}
