import "../Library/library";
import { Loading, useState } from "../Library/library";

export default function CreateCategory() {
  const [form, setForm] = useState({});
  const [categories, setCategories] = useState({});
  const [loading, setLoading] = useState(false);

  async function fetchData() {
    try {
      const response = await axios.get(`${baseUrl}/${CATEGORIES}`, configget);
      console.log(response.data);
      setCategories(response.data);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchData();
  }, [deleteCategory]);

  return (
    <>
      {loading && <Loading />}

      <div className="container mt-5 mb-5">
        <h2>Add Category</h2>
        <form>
          <div class="form-group mb-3">
            <label for="name">Name</label>
            <input
              type="text"
              name="name"
              id="name"
              value=" "
              class="form-control"
            />
          </div>

          <div class="form-group mb-3">
            <label for="category_id">Category Parent</label>
            <select
              name="category_id"
              class="form-control  @error('parent_id') is-invalid @enderror"
              style={{ height: "calc(2.8rem + 10px)" }}
            >
              <option value=""> No paranet</option>

              <option value=""> </option>
            </select>
          </div>

          <div class="form-group mb-3">
            <label for="description">Description:</label>
            <textarea
              name="description"
              value=""
              class="form-control "
            ></textarea>
          </div>

          <div class="form-group mb-3">
            <label for="art_path">Image:</label>
            <input type="file" name="imag" id="image" class="form-control" />
          </div>

          <td>
            <img src="" height="50" />
          </td>

          <div class="form-group mb-3">
            <label for="status">Status:</label>
            <div>
              <label>
                <input type="radio" name="status" value="active" />
                Active
              </label>
              <label>
                <input type="radio" name="status" value="inactive" />
                inActive
              </label>
            </div>
          </div>

          <button type="submit" class="btn btn-primary">
            ADD
          </button>
        </form>
      </div>
    </>
  );
}
