import { useState, useEffect } from "react";
import axios from "axios";
import { baseUrl, User, REGISTER } from "../../Api/Api"; // تأكد من تحديد المسار الصحيح لجلب بيانات المستخدم
import "../Auth/Auth.css";
import Loading from "../../components/Loading/Loading";

export default function UserProfile() {
  const [loading, setLoading] = useState(false);
  const [errorRegister, setErrorRegister] = useState("");
  const [form, setForm] = useState({
    email: "",
    username: "",
    password: "",
    birthday: "",
    last_name: "",
    first_name: "",
    gender: "",
    address: "",
    state: "",
    city: "",
    postal_code: "",
    country: "",
  });

  useEffect(() => {
    const userId = "id_of_the_user";
    fetchUserData(userId);
  }, []);

  async function fetchUserData(userId) {
    setLoading(true);
    try {
      const response = await axios.get(`${baseUrl}/${User}/${userId}`);
      if (response.status === 200) {
        setForm(response.data);
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
      setErrorRegister(
        error.response?.data?.errors || "Failed to load user data"
      );
    }
    setLoading(false);
  }

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);

    try {
      const result = await axios.post(`${baseUrl}/${REGISTER}`, form);
      if (result.status === 200) {
        // Handle success, perhaps reset form or redirect
        setForm({});
      }
    } catch (error) {
      console.error("Submission error:", error);
      setErrorRegister(
        error.response?.data?.errors || "Error occurred during registration"
      );
    }
    setLoading(false);
  }

  return (
    <>
      {loading && <Loading />}

      <div className="container mt-5 mb-5">
        <h2>Profile Registration</h2>

        {errorRegister && (
          <div className="errorregister">
            {Object.keys(errorRegister).map((key) =>
              errorRegister[key].map((error, index) => (
                <span key={`${key}-${index}`}>{error}</span>
              ))
            )}
          </div>
        )}

        <div className="form-container">
          <form className="needs-validation" onSubmit={handleSubmit}>
            {/* إضافة العناصر المدخلة الأخرى مع الخصائص المناسبة */}
            {/* استمر في تحديث النموذج بناءً على حالة الفورم */}
          </form>
        </div>
      </div>
    </>
  );
}
