import {
  CATEGORIES,
  baseUrl,
  DELETECATEGORY,
  imageurl,
  PRODUCTS,
  DELETEPRODUCTS,
} from "../../../Api/Api.js";
// في أي ملف React آخر
import {
  Loading,
  FontAwesomeIcon,
  faPenToSquare,
  faTrash,
  useState,
  useEffect,
  Link,
  axios,
  Cookie,
  configget,
  useNavigate,
  useParams,
} from "../Library/library.js";

export default function Products() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [products, setProducts] = useState([]);
  const [deleteProducts, setDeleteProducts] = useState(false);

  async function fetchData() {
    try {
      const response = await axios.get(`${baseUrl}/${PRODUCTS}`, configget);
      console.log(response.data);
      setProducts(response.data.categories);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchData();
  }, [deleteProducts]);

  async function handelDelete(id) {
    try {
      setLoading(true);
      var response = await axios.delete(
        `${baseUrl}/${DELETEPRODUCTS}/${id}`,
        configget
      );
      if (response.status === 200) {
        setDeleteProducts((prev) => !prev);
        setLoading(false);
      }
    } catch (error) {
      console.log(error);
      setLoading(false);
    }
  }
  console.log(imageurl);
  return (
    <>
      {loading && <Loading />}
      <div className="bg-white  w-100 p-2">
        <h1>products Page</h1>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Name</th>
              <th scope="col">Category</th>
              <th scope="col">Image</th>
              <th scope="col">ِAction</th>
            </tr>
          </thead>
          <tbody>
            {products &&
              products.map((item, index) => (
                <tr key={index}>
                  <td>{item.id}</td>
                  <th scope="row">{item.product_name}</th>
                  <th scope="row">{item.category.name}</th>

                  <th scope="row">
                    <img
                      src={`${imageurl}/${item.product_image}`}
                      alt={item.name}
                      style={{
                        width: "100px",
                        height: "100px",
                        border: "1px solid black",
                      }}
                    />
                  </th>

                  <td>
                    <div className="d-flex align-items-center gap-2">
                      <Link to={`../editCategory/${item.id}`}>
                        <FontAwesomeIcon
                          fontSize={"19px"}
                          color="blue"
                          icon={faPenToSquare}
                        />
                      </Link>
                      <FontAwesomeIcon
                        fontSize={"19px"}
                        onClick={() => handelDelete(`${item.id}`)}
                        color="red"
                        cursor={"pointer"}
                        icon={faTrash}
                      />
                    </div>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </>
  );
}
