import { useState, useEffect } from "react";
import axios from "axios";
import { baseUrl, User, editProfile } from "../../Api/Api";
import "../Auth/Auth.css";
import Loading from "../../components/Loading/Loading";
import { useParams } from "react-router-dom";
import Cookie from "cookie-universal";

export default function UserProfile() {
  const { id } = useParams();
  console.log(id);
  const [loading, setLoading] = useState(false);
  const [errorRegister, setErrorRegister] = useState("");
  const [form, setForm] = useState({
    email: "",
    username: "",
    password: "",
    birthday: "",
    last_name: "",
    first_name: "",
    gender: "",
    address: "",
    state: "",
    city: "",
    postal_code: "",
    country: "",
  });
  const cookie = Cookie();
  const token = cookie.get("admin-token");
  useEffect(() => {
    const userId = id;
    fetchUserData(userId);
  }, []);

  async function fetchUserData(userId) {
    setLoading(true);
    try {
      const response = await axios.get(`${baseUrl}/${User}/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });
      console.log(response);
      if (response.status === 200) {
        setForm(response.data);
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
      setErrorRegister(
        error.response?.data?.errors || "Failed to load user data"
      );
    }
    setLoading(false);
  }

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);

    try {
      const result = await axios.put(`${baseUrl}/${editProfile}`, form);
      console.log(result);
      if (result.status === 200) {
        setForm({});
      }
    } catch (error) {
      console.error("Submission error:", error);
      setErrorRegister(
        error.response?.data?.errors || "Error occurred during registration"
      );
    }
    setLoading(false);
  }

  return (
    <>
      {loading && <Loading />}
      <div className="container mt-5 mb-5">
        <h2>Profile </h2>
        {errorRegister && (
          <div className="errorregister">
            {Object.keys(errorRegister).map(
              (key) =>
                Array.isArray(errorRegister[key]) ? (
                  errorRegister[key].map((error, index) => (
                    <span key={`${key}-${index}`}>{error}</span>
                  ))
                ) : (
                  <span key={`error-${key}`}>{errorRegister[key]}</span>
                ) // Handle non-array values gracefully
            )}
          </div>
        )}
        <div className="form-container">
          <form className="needs-validation" onSubmit={handleSubmit}>
            {/* Add form fields here */}
          </form>
        </div>
      </div>
    </>
  );
}
