import { CATEGORIES, baseUrl } from "../../Api/Api";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../Auth/Auth.css";
import Loading from "../../components/Loading/Loading";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPenToSquare, faTrash } from "@fortawesome/free-solid-svg-icons";
import { useParams } from "react-router-dom";
import Cookie from "cookie-universal";
import { useNavigate } from "react-router-dom";

export default function Categories() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [categories, setcategories] = useState([]);
  const [deleteCategory, setDeleteCategory] = useState(false);
  const cookie = Cookie();
  const token = cookie.get("admin-token");
  const configget = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "application/json",
    },
  };

  async function fetchData() {
    try {
      const response = await axios.get(`${baseUrl}/${CATEGORIES}`, configget);
      console.log(response.data);
      setcategories(response.data);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchData();
  }, [deleteCategory]);

  async function handelDelete(id) {
    try {
      setLoading(true);
      var response = await axios.delete(
        `${baseUrl}/${DELETECATEGORY}/${id}`,
        configget
      );
      if (response.status === 200) {
        setDeleteCategory((prev) => !prev);
        setLoading(false);
      }
    } catch (error) {
      console.log(error);
      setLoading(false);
    }
  }

  return (
    <>
      {loading && <Loading />}
      <div className="bg-white  w-100 p-2">
        <h1>Categories Page</h1>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Name</th>
              <th scope="col">ŸêAction</th>
            </tr>
          </thead>
          <tbody>
            {categories &&
              categories.map((item, index) => (
                <tr key={index}>
                  <td>{item.id}</td>
                  <th scope="row">{item.name}</th>
                  <td>{item.email}</td>
                  <td>
                    <div className="d-flex align-items-center gap-2">
                      <Link to={`../userProfile/${item.id}`}>
                        <FontAwesomeIcon
                          fontSize={"19px"}
                          color="blue"
                          icon={faPenToSquare}
                        />
                      </Link>
                      <FontAwesomeIcon
                        fontSize={"19px"}
                        onClick={() => handelDelete(`${item.id}`)}
                        color="red"
                        cursor={"pointer"}
                        icon={faTrash}
                      />
                    </div>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </>
  );
}
